INSERT INTO `topic_comment` VALUES (4854799, 1189462, 7950, '有些法律制裁不了的人渣，确实需要其他渠道对其进行制裁，毕竟法律保障的只是最低底线', 1681877304000, NULL, 4854537, 4854537);
INSERT INTO `topic_comment` VALUES (4854802, 1189462, 251379, '应该是人类太坏了吧！我保证全世界任何一条狗都做不出这种事！', 1681877697000, NULL, 4854612, 4854612);
