INSERT INTO `topic_comment` VALUES (4855064, 1189462, 148328, '对于游戏店来说。无论你是老用户还是新用户，卖出了硬件就不愁没人你不买软件。因为他们都会说同样的话：玩完了卖给我，亏几十而已\n至于这个期限多久。那就游戏店说了算了', 1681904727000, NULL, 4854543, 4854842);
